import React from 'react';
import { AuthProvider, useAuth } from './web/AuthContext';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Navbar from './web/Design/Navbar';
import Login from './web/cmpts/Login';
import Patient from './web/Users/Patient';
import Doctor from './web/Users/Doctor';
import Admin from './web/Users/Admin';

const PrivateRoute = ({ role, element }) => {
  const { user } = useAuth();

  if (!user || user.role !== role) {
    // Redirect to login if the user is not logged in or has the wrong role
    return <Navigate to="/login" replace />; // Use Navigate component to redirect with replace
  }

  return element;
};

const App = () => {
  return (
    <AuthProvider>
      <Router>
        <Navbar />
        <Routes>
          <Route path="/login" element={<Login />} />
          <Route
            path="/patient/*"
            element={<PrivateRoute role="patient" element={<Patient />} />}
          />
          <Route
            path="/doctor/*"
            element={<PrivateRoute role="doctor" element={<Doctor />} />}
          />
          <Route
            path="/admin/*"
            element={<PrivateRoute role="admin" element={<Admin />} />}
          />
        </Routes>
      </Router>
    </AuthProvider>
  );
};

export default App;
