// App.js
import React from 'react';
import { AuthProvider, useAuth  } from './web/AuthContext';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './web/Design/Navbar';
import Login from './web/cmpts/Login';
import Logout from './web/cmpts/Logout';
import Patient from './web/Users/Patient';
import Doctor from './web/Users/Doctor';
import Admin from './web/Users/Admin';

const PrivateRoute = ({ role, children }) => {
  const { user } = useAuth();

  if (!user || user.role !== role) {
    // Redirect to login if the user is not logged in or has the wrong role
    return null; // You can also use 'return <Redirect to="/login" />' if using React Router Redirect component
  }

  return children;
};

const App = () => {
  return (
    <AuthProvider>
      <Router>
        <Navbar />
        <Routes>
          <Route path="/login" element={<Login />} />
          <PrivateRoute path="/patient" role="patient">
            <Route index element={<Patient />} />
          </PrivateRoute>
          <PrivateRoute path="/doctor" role="doctor">
            <Route index element={<Doctor />} />
          </PrivateRoute>
          <PrivateRoute path="/admin" role="admin">
            <Route index element={<Admin />} />
          </PrivateRoute>
        </Routes>
      </Router>
    </AuthProvider>
  );
};

export default App;
