import React, { useState, useEffect } from 'react';
import { useAuth } from '../AuthContext';
import { getPatientPosts, replyToPreMedicalForm, createComment } from '../apiService';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';

const DoctorPage = () => {
  const { user } = useAuth();
  const [selectedDoctorId, setSelectedDoctorId] = useState(null);
  const [patientPosts, setPatientPosts] = useState([]);
  const [commentContent, setCommentContent] = useState('');

  useEffect(() => {
    fetchPatientPosts();
  }, []);

  const fetchPatientPosts = async () => {
    try {
      const posts = await getPatientPosts();
      setPatientPosts(posts);
    } catch (error) {
      console.error(error);
    }
  };

  const handleReply = async (postId, doctorUserId) => {
    try {
      await replyToPreMedicalForm(postId);
      setSelectedDoctorId(doctorUserId);
    } catch (error) {
      console.error(error);
    }
  };

  const handleComment = async (postId, patientUserId) => {
    try {
      await createComment({
        content: commentContent,
        author: user.id,
        blog_post: postId,
        commenter_role: 'doctor',
        receiver: patientUserId,
      });

      setCommentContent('');
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h2>Patient Posts</h2>
      {patientPosts.map((post) => (
        <Card key={post.id} style={{ width: '18rem', marginBottom: '20px' }}>
          <Card.Body>
            <Card.Title>{post.title}</Card.Title>
            <Card.Text>{post.content}</Card.Text>
            {!post.form_filled_by_doctor && (
              <Button onClick={() => handleReply(post.id, post.patient)}>Reply to Pre-Medical Form</Button>
            )}
            <textarea
              placeholder="Type your comment"
              value={commentContent}
              onChange={(e) => setCommentContent(e.target.value)}
            />
            <Button onClick={() => handleComment(post.id, post.patient)}>Comment</Button>
          </Card.Body>
        </Card>
      ))}
    </div>
  );
};

export default DoctorPage;
