import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate, useNavigate } from 'react-router-dom';
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import Button from 'react-bootstrap/Button';
import Login from './Login'; // Import your Login component
import Register from './Register'; // Import your Register component
import PatientDashboard from './PatientDashboard'; // Import your PatientDashboard component
import DoctorDashboard from './DoctorDashboard'; // Import your DoctorDashboard component
import AdminDashboard from './AdminDashboard'; // Import your AdminDashboard component

function App() {
  const [isLogin, setIsLogin] = useState(true);
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  const handleLogin = (userData) => {
    // Simulate a login action and set the user data
    setUser(userData);

    // Replace with your logic to determine the user's role
    const userRole = getUserRole();

    if (userRole === 'patient') {
      navigate('/patient-dashboard');
    } else if (userRole === 'doctor') {
      navigate('/doctor-dashboard');
    } else if (userRole === 'admin') {
      navigate('/admin-dashboard');
    }
  };

  const handleLogout = () => {
    // Simulate a logout action
    setUser(null);
    navigate('/login'); // Redirect to the login page after logout
  };

  const getUserRole = () => {
    // Replace this with your actual logic to determine the user's role
    // For example, you can get this information from the user object
    if (user && user.role) {
      return user.role;
    }
    // If no role is defined, you can return a default role
    return 'patient'; // Replace with 'doctor', 'admin', or the default role
  };

  return (
    <Router>
      <div>
        <Navbar expand="lg" className="bg-body-tertiary">
          <Container>
            <Navbar.Brand href="/">Your App</Navbar.Brand>
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
            <Navbar.Collapse id="basic-navbar-nav">
              <Nav className="me-auto">
                {/* Add navigation links for other pages */}
              </Nav>
              {user ? (
                <Navbar.Text>
                  Signed in as: {user.name}
                  <Button variant="link" onClick={handleLogout}>
                    Logout
                  </Button>
                </Navbar.Text>
              ) : (
                <Button variant="primary" onClick={() => setIsLogin(!isLogin)}>
                  {isLogin ? 'Register' : 'Login'}
                </Button>
              )}
            </Navbar.Collapse>
          </Container>
        </Navbar>

        <Routes>
          <Route path="/login" element={<Login onLogin={handleLogin} />} />
          <Route path="/register" element={<Register onRegister={handleLogin} />} />
          <Route path="/patient-dashboard" element={<PatientDashboard />} />
          <Route path="/doctor-dashboard" element={<DoctorDashboard />} />
          <Route path="/admin-dashboard" element={<AdminDashboard />} />
          <Route path="/" element={<Navigate to="/login" />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
