// CombinedForm.js
import React, { useState } from 'react';
import { useAuth } from '../../AuthContext';
import { fillPreMedicalForm, createBlogPost } from '../../apiService';
import './CombinedForm.css'; // Import the stylesheet
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

const CombinedForm = () => {
  const { user } = useAuth();

  const [symptoms, setSymptoms] = useState('');
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Submit both types of forms simultaneously
      const [blogPostResponse, preMedicalFormResponse] = await Promise.all([
        createBlogPost({ title, content, author: user.id }),
        fillPreMedicalForm({ symptoms, patient: user.id }),
      ]);

      // Handle success for both types
      console.log('Blog Post Response:', blogPostResponse);
      console.log('Pre-Medical Form Response:', preMedicalFormResponse);

      // You may want to add success messages or redirects
    } catch (error) {
      console.error(error);
      // Handle errors, e.g., display an error message to the user
    }
  };

  return (
    <div className="container mt-5">
      <div className="form-container mt-5">
        <h2 className="mb-4">Create a Post and Fill Pre-Medical Form</h2>
        <Form className="combined-form" onSubmit={handleSubmit}>
          <Form.Group className="mb-3">
            <Form.Label>Title:</Form.Label>
            <Form.Control type="text" value={title} onChange={(e) => setTitle(e.target.value)} />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Content:</Form.Label>
            <Form.Control as="textarea" rows={3} value={content} onChange={(e) => setContent(e.target.value)} />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Symptoms:</Form.Label>
            <Form.Control as="textarea" rows={3} value={symptoms} onChange={(e) => setSymptoms(e.target.value)} />
          </Form.Group>
          <Button variant="primary" type="submit">
            Submit
          </Button>
        </Form>
      </div>
    </div>
  );
};

export default CombinedForm;
