// PatientPostView.js
import React, { useState, useEffect } from 'react';
import { useAuth } from '../AuthContext';
import { getMessages, createMessage } from '../apiService';
import Chat from '../Chat/Chat';

const PatientPostView = () => {
  const { user } = useAuth();
  const [messages, setMessages] = useState([]);
  const [selectedDoctorId, setSelectedDoctorId] = useState(null);

  useEffect(() => {
    fetchMessages();
  }, [selectedDoctorId]);

  const fetchMessages = async () => {
    try {
      const receivedMessages = await getMessages(selectedDoctorId);
      setMessages(receivedMessages);
    } catch (error) {
      console.error(error);
    }
  };

  const handleMessage = async (content) => {
    try {
      if (!selectedDoctorId) {
        console.error('Please select a doctor before sending a message.');
        return;
      }

      await createMessage({ content, sender: user.id, receiver: selectedDoctorId });
      fetchMessages();
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h2>Your Messages</h2>
      {messages.map((message) => (
        <div key={message.id}>
          <p>{message.content}</p>
        </div>
      ))}
      <textarea placeholder="Type your message" />
      <button onClick={() => setSelectedDoctorId(/* selected doctor's id */)}>Select Doctor</button>
      <button onClick={() => handleMessage('content')}>Send Message</button>

      {/* Render the Chat component for the selected doctor */}
      {selectedDoctorId && <Chat receiverId={selectedDoctorId} />}
    </div>
  );
};

export default PatientPostView;
