// apiService.js
import axios from 'axios';

axios.defaults.xsrfCookieName = 'csrftoken';
axios.defaults.xsrfHeaderName = 'X-CSRFToken';
axios.defaults.withCredentials = true;
const BASE_URL = process.env.REACT_APP_BACKEND_URL || 'http://127.0.0.1:8000/';
  // Replace with your Django backend URL

const apiService = axios.create({
  baseURL: BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

export const login = async (username, password) => {
  try {
    const response = await apiService.post('/login/', { username, password });
    return response.data;
  } catch (error) {
    if (error.response) {
      throw error.response.data;
    } else if (error.request) {
      throw 'Network error. Please try again.';
    } else {
      throw 'An error occurred. Please try again later.';
    }
  }
};

export const logout = async (username, password) => {
    try {
      const response = await apiService.post('/logout/', { username, password });
      return response.data;
    } catch (error) {
      if (error.response) {
        throw error.response.data;
      } else if (error.request) {
        throw 'Network error. Please try again.';
      } else {
        throw 'An error occurred. Please try again later.';
      }
    }
  };

export const getUserDetails = async () => {
  try {
    const response = await apiService.get('/user-details/');
    return response.data;
  } catch (error) {
    if (error.response) {
      throw error.response.data;
    } else if (error.request) {
      throw 'Network error. Please try again.';
    } else {
      throw 'An error occurred. Please try again later.';
    }
  }
};
