export function authHeader() {
    // Get the user token from local storage
    const user = JSON.parse(localStorage.getItem('user'));

    if (user && user.token) {
        // Return an authorization header with the user's token
        return { 'Authorization': `Token ${user.token}` };
    } else {
        return {};
    }
}
