    // Registration actions
    REGISTER_REQUEST: 'USER_REGISTER_REQUEST',
    REGISTER_SUCCESS: 'USER_REGISTER_SUCCESS',
    REGISTER_FAILURE: 'USER_REGISTER_FAILURE',

    // Logout action
    LOGOUT: 'USER_LOGOUT',

    // Update profile actions
    UPDATE_PROFILE_REQUEST: 'USER_UPDATE_PROFILE_REQUEST',
    UPDATE_PROFILE_SUCCESS: 'USER_UPDATE_PROFILE_SUCCESS',
    UPDATE_PROFILE_FAILURE: 'USER_UPDATE_PROFILE_FAILURE',

    // Error and notification actions
    SHOW_ERROR: 'USER_SHOW_ERROR',
    SHOW_NOTIFICATION: 'USER_SHOW_NOTIFICATION',
    HIDE_ERROR: 'USER_HIDE_ERROR',
    HIDE_NOTIFICATION: 'USER_HIDE_NOTIFICATION',
