import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Admin() {
  const [users, setUsers] = useState([]);
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    is_admin: false, // You can add more fields if needed
  });

  useEffect(() => {
    // Fetch user data from your Django API
    axios.get('http://127.0.0.1:8000/api/users/')
      .then((response) => {
        setUsers(response.data); // Ensure the response is an array
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  }, []);

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleUserRegistration = () => {
    // Make an API request to register a new user
    axios.post('http://127.0.0.1:8000/register/', formData , {
      withCredentials: true,
      headers: {
          'Content-Type': 'application/json',
          
      },
  })
      .then((response) => {
        // Update the user list with the newly registered user
        setUsers([...users, response.data]);
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  };

  return (
    <div>
      <h1>Admin Panel</h1>
      
      <h2>User Registration</h2>
      <form>
        <input
          type="text"
          name="username"
          placeholder="Username"
          value={formData.username}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleInputChange}
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={formData.password}
          onChange={handleInputChange}
        />
        <label>
          Admin
          <input
            type="checkbox"
            name="is_admin"
            checked={formData.is_admin}
            onChange={handleInputChange}
          />
        </label>
        <button type="button" onClick={handleUserRegistration}>
          Register User
        </button>
      </form>

      <h2>User List</h2>
      <ul>
        {users.map((user) => (
          <li key={user.id}>
            {user.username} - {user.email} {user.is_admin ? '(Admin)' : ''}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Admin;
