// DoctorPage.js
import React, { useState, useEffect } from 'react';
import { useAuth } from '../AuthContext';
import { getPatientSymptoms, getPatientBlogPosts, replyToPreMedicalForm, createComment } from '../apiService';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';

const DoctorPage = () => {
  const { user } = useAuth();
  const [selectedDoctorId, setSelectedDoctorId] = useState(null);
  const [patientSymptoms, setPatientSymptoms] = useState([]);
  const [patientBlogPosts, setPatientBlogPosts] = useState([]);
  const [commentContent, setCommentContent] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchPatientData();
  }, []);

  const fetchPatientData = async () => {
    try {
      const symptoms = await getPatientSymptoms();
      const blogPosts = await getPatientBlogPosts();

      setPatientSymptoms(symptoms);
      setPatientBlogPosts(blogPosts);
    } catch (error) {
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  const handleReply = async (postId, doctorUserId) => {
    try {
      await replyToPreMedicalForm(postId);
      setSelectedDoctorId(doctorUserId);
    } catch (error) {
      console.error(error);
    }
  };

  const handleComment = async (postId, patientUserId) => {
    try {
      await createComment({
        content: commentContent,
        author: user.id,
        blog_post: postId,
        commenter_role: 'doctor',
        receiver: patientUserId,
      });

      setCommentContent('');
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h2>Patient Symptoms</h2>
      {loading ? (
        <p>Loading...</p>
      ) : (
        patientSymptoms.map((symptom) => (
          <Card key={symptom.id} style={{ width: '18rem', marginBottom: '20px' }}>
            <Card.Body>
              <Card.Title>{symptom.title}</Card.Title>
              <Card.Text>{symptom.content}</Card.Text>
              {/* Add rendering for symptom comments if needed */}
            </Card.Body>
          </Card>
        ))
      )}

      <h2>Patient Blog Posts</h2>
      {loading ? (
        <p>Loading...</p>
      ) : (
        patientBlogPosts.map((post) => (
          <Card key={post.id} style={{ width: '18rem', marginBottom: '20px' }}>
            <Card.Body>
              <Card.Title>{post.title}</Card.Title>
              <Card.Text>{post.content}</Card.Text>
              {post.comments && post.comments.map((comment) => (
                <div key={comment.id} className="comment-container">
                  <p>{comment.content}</p>
                  <p>Patient ID: {comment.author}</p>
                </div>
              ))}
              {!post.form_filled_by_doctor && (
                <Button onClick={() => handleReply(post.id)}>Reply to Pre-Medical Form</Button>
              )}
              <textarea
                placeholder="Type your comment"
                value={commentContent}
                onChange={(e) => setCommentContent(e.target.value)}
              />
              <Button onClick={() => handleComment(post.id, post.patient)}>Comment</Button>
            </Card.Body>
          </Card>
        ))
      )}
    </div>
  );
};

export default DoctorPage;
