// DoctorPage.js
import React, { useState, useEffect } from 'react';
import { useAuth } from '../AuthContext';
import { getPatientBlogPosts, replyToPreMedicalForm, createComment, getPatientSymptoms, createMessage } from '../apiService';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Offcanvas from 'react-bootstrap/Offcanvas';

const DoctorPage = () => {
  const { user } = useAuth();
  const [selectedPatient, setSelectedPatient] = useState(null);
  const [patientData, setPatientData] = useState([]);
  const [commentContents, setCommentContents] = useState({});
  const [showMessageInput, setShowMessageInput] = useState(false);

  useEffect(() => {
    fetchPatientData();
  }, []);

  const fetchPatientData = async () => {
    try {
      const [blogPosts, symptomsData] = await Promise.all([getPatientBlogPosts(), getPatientSymptoms()]);

      const mergedData = blogPosts.map((post, index) => ({
        ...post,
        symptoms: symptomsData[index] ? symptomsData[index].symptoms : null,
      }));

      setPatientData(mergedData);
      const initialCommentContents = {};
      mergedData.forEach((post) => {
        initialCommentContents[post.patient] = ''; // Use patient ID as the key
      });
      setCommentContents(initialCommentContents);
    } catch (error) {
      console.error(error);
    }
  };

  const handleReply = async (postId, patientUserId) => {
    try {
      await replyToPreMedicalForm(postId);
      setSelectedPatient(patientUserId); // Set the patient ID, not post ID
    } catch (error) {
      console.error(error);
    }
  };

  const handleComment = async (postId, patientUserId) => {
    try {
      const content = commentContents[postId];
      await createComment({
        content,
        author: user.id,
        blog_post: postId,
        commenter_role: 'doctor',
        receiver: patientUserId,
      });

      setCommentContents((prevContents) => ({
        ...prevContents,
        [postId]: '',
      }));
    } catch (error) {
      console.error(error);
    }
  };

  const handleSendMessage = async () => {
    try {
      console.log('Selected patient:', selectedPatient);
  
      if (selectedPatient) {
        const selectedPost = patientData.find((post) => post.patient === selectedPatient);
  
        console.log('Selected post:', selectedPost);
  
        if (selectedPost) {
          await createMessage({
            content: commentContents[selectedPatient],
            sender: user.id, // Use the doctor's user ID as the sender
            receiver: selectedPost.author, // Use the patient's user ID as the receiver
          });
  
          console.log('Message sent successfully.');
  
          // Clear the comment content for the specific patient
          setCommentContents((prevContents) => ({
            ...prevContents,
            [selectedPatient]: '',
          }));
  
          // Close the message input OffCanvas
          setShowMessageInput(false);
        } else {
          console.error('Selected patient not found.');
        }
      } else {
        console.error('No selected patient to send a message.');
      }
    } catch (error) {
      console.error(error);
    }
  };
  
  const handleCardClick = (patientId) => {
    setSelectedPatient(patientId);
    setShowMessageInput(true); // Show the OffCanvas for sending messages
  };

  return (
    <div>
      <h2>Patient Posts</h2>
      <Row>
        {patientData.map((post) => (
          <Col key={post.id} sm={6} md={4} lg={3} style={{ marginBottom: '20px' }}>
            <Card>
              <Card.Body>
                <Card.Title>{post.title}</Card.Title>
                <Card.Text><strong>Patient Symptoms:</strong> {post.symptoms}</Card.Text>
                <Card.Text><strong>Post Content:</strong> {post.content}</Card.Text>
                {!post.form_filled_by_doctor && (
                  <Button onClick={() => handleReply(post.id, post.patient)}>Reply to Pre-Medical Form</Button>
                )}
                <Button onClick={() => setShowMessageInput(true)}>Send Message</Button>
                <textarea
                  placeholder="Type your comment"
                  value={commentContents[post.id]}
                  onChange={(e) => setCommentContents((prevContents) => ({
                    ...prevContents,
                    [post.id]: e.target.value,
                  }))}
                />
                <Button onClick={() => handleComment(post.id, post.patient)}>Comment</Button>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
      <OffCanvas show={showMessageInput} onHide={() => setShowMessageInput(false)}>
      <OffCanvas.Header closeButton>
        <OffCanvas.Title>Send Message</OffCanvas.Title>
      </OffCanvas.Header>
      <OffCanvas.Body>
        <textarea
          placeholder="Type your message"
          value={commentContents[selectedPatient] || ''}
          onChange={(e) => setCommentContents((prevContents) => ({
            ...prevContents,
            [selectedPatient]: e.target.value,
          }))}
        />
        <Button onClick={handleSendMessage}>Send Message</Button>
      </OffCanvas.Body>
    </OffCanvas>
    </div>
  );
};

export default DoctorPage;
