// DoctorPage.js
import React, { useState, useEffect } from 'react';
import { useAuth } from '../AuthContext';
import { getPatientBlogPosts, replyToPreMedicalForm, createComment, getPatientSymptoms, getComments } from '../apiService';  // Update the import statement
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Offcanvas from 'react-bootstrap/Offcanvas';

const DoctorPage = () => {
  const { user } = useAuth();
  const [selectedDoctorId, setSelectedDoctorId] = useState(null);
  const [patientData, setPatientData] = useState([]);
  const [commentContents, setCommentContents] = useState({});
  const [selectedBlogPostId, setSelectedBlogPostId] = useState(null);  // State to store the selected blog post ID
  const [comments, setComments] = useState([]);  // State to store comments for the selected blog post
  const [showComments, setShowComments] = useState(false);

  useEffect(() => {
    fetchPatientData();
  }, []);

  useEffect(() => {
    // Fetch comments when the selectedBlogPostId changes
    if (selectedBlogPostId) {
      fetchComments(selectedBlogPostId);
    }
  }, [selectedBlogPostId]);

  const fetchPatientData = async () => {
    try {
      const [blogPosts, symptomsData] = await Promise.all([getPatientBlogPosts(), getPatientSymptoms()]);
      const mergedData = blogPosts.map((post, index) => ({
        ...post,
        symptoms: symptomsData[index] ? symptomsData[index].symptoms : null,
      }));

      setPatientData(mergedData);
      const initialCommentContents = {};
      mergedData.forEach((post) => {
        initialCommentContents[post.id] = '';
      });
      setCommentContents(initialCommentContents);
    } catch (error) {
      console.error(error);
    }
  };

  const handleReply = async (postId, doctorUserId) => {
    try {
      await replyToPreMedicalForm(postId);
      setSelectedDoctorId(doctorUserId);
      // Set the selected blog post ID when replying to a pre-medical form
      setSelectedBlogPostId(postId);
    } catch (error) {
      console.error(error);
    }
  };

  const handleComment = async (postId, patientUserId) => {
    try {
      const content = commentContents[postId];
      await createComment({
        content,
        author: user.id,
        blog_post: postId,
        commenter_role: 'doctor',
        receiver: patientUserId,
      });

      setCommentContents((prevContents) => ({
        ...prevContents,
        [postId]: '',
      }));
    } catch (error) {
      console.error(error);
    }
  };

  const fetchComments = async (blogPostId) => {
    try {
      const commentsData = await getComments(blogPostId);
      setComments(commentsData);
    } catch (error) {
      console.error(error);
    }
  };


  const handleShowComments = (postId) => {
    setSelectedBlogPostId(postId);
    setShowComments(true);
  };

  const handleCloseComments = () => {
    setSelectedBlogPostId(null);
    setShowComments(false);
  };


  return (
    <div>
      <h2>Patient Posts</h2>
      <Row>
        {patientData.map((post) => (
          <Col key={post.id} sm={6} md={4} lg={3} style={{ marginBottom: '20px' }}>
            <Card>
              <Card.Body>
                <Card.Title>{post.title}</Card.Title>
                <Card.Text><strong>Patient Symptoms:</strong> {post.symptoms}</Card.Text>
                <Card.Text><strong>Post Content:</strong> {post.content}</Card.Text>
                {!post.form_filled_by_doctor && (
                  <Button onClick={() => handleReply(post.id, post.patient)}>Reply to Pre-Medical Form</Button>
                )}
                <Button onClick={() => handleShowComments(post.id)}>Show Comments</Button>
                <textarea
                  placeholder="Type your comment"
                  value={commentContents[post.id]}
                  onChange={(e) => setCommentContents((prevContents) => ({
                    ...prevContents,
                    [post.id]: e.target.value,
                  }))}
                />
                <Button onClick={() => handleComment(post.id, post.patient)}>Comment</Button>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>

      {/* Offcanvas for displaying comments */}
      <Offcanvas show={showComments} onHide={handleCloseComments}>
        <Offcanvas.Header closeButton>
          <Offcanvas.Title>Comments</Offcanvas.Title>
        </Offcanvas.Header>
        <Offcanvas.Body>
          {/* Display comments for the selected pre-medical form */}
          {selectedBlogPostId && (
            <div>
              <h2>Comments</h2>
              <ul className="list-unstyled">
                {comments.map((comment) => (
                  <li key={comment.id} className="mb-3">
                    <strong>{comment.commenter_role === 'doctor' ? 'Doctor' : 'Patient'}</strong>: {comment.content}
                    {/* Add other comment details as needed */}
                  </li>
                ))}
              </ul>
            </div>
          )}
        </Offcanvas.Body>
      </Offcanvas>
    </div>
  );
};

export default DoctorPage;
