import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Link, Switch, Redirect, useHistory } from 'react-router-dom';
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import Button from 'react-bootstrap/Button';
import Login from './Login'; // Import your Login component
import Register from './Register'; // Import your Register component
import PatientDashboard from './PatientDashboard'; // Import your PatientDashboard component
import DoctorDashboard from './DoctorDashboard'; // Import your DoctorDashboard component
import AdminDashboard from './AdminDashboard'; // Import your AdminDashboard component

function App() {
  const [isLogin, setIsLogin] = useState(true);
  const [user, setUser] = useState(null);
  const history = useHistory();

  const handleLogin = (userData) => {
    setUser(userData);
    history.push('/dashboard');
  };

  const handleLogout = () => {
    setUser(null);
    history.push('/login');
  };

  const renderAuthButtons = () => {
    if (user) {
      return (
        <>
          <Navbar.Text>Signed in as: {user.name}</Navbar.Text>
          <Button variant="link" onClick={handleLogout}>Logout</Button>
        </>
      );
    } else {
      return (
        <>
          <Button variant="primary" onClick={() => setIsLogin(!isLogin)}>
            {isLogin ? 'Register' : 'Login'}
          </Button>
        </>
      );
    }
  };

  return (
    <Router>
      <div>
        <Navbar expand="lg" className="bg-body-tertiary">
          <Container>
            <Navbar.Brand as={Link} to="/">Your App</Navbar.Brand>
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
            <Navbar.Collapse id="basic-navbar-nav">
              <Nav className="me-auto">
                <Nav.Link as={Link} to="/home">Home</Nav.Link>
                {/* Add other navigation links for different pages */}
              </Nav>
              <Navbar.Collapse className="justify-content-end">
                {renderAuthButtons()}
              </Navbar.Collapse>
            </Navbar.Collapse>
          </Container>
        </Navbar>

        <Switch>
          <Route exact path="/login">
            {user ? <Redirect to="/dashboard" /> : <Login onLogin={handleLogin} />}
          </Route>
          <Route exact path="/register">
            {user ? <Redirect to="/dashboard" /> : <Register onLogin={handleLogin} />}
          </Route>
          <Route path="/dashboard">
            {user ? (
              user.role === 'patient' ? <PatientDashboard user={user} /> :
              user.role === 'doctor' ? <DoctorDashboard user={user} /> :
              user.role === 'admin' ? <AdminDashboard user={user} /> : null
            ) : <Redirect to="/login" />}
          </Route>
          <Redirect from="/" to="/login" />
        </Switch>
      </div>
    </Router>
  );
}

export default App;
