import React, { useState, useEffect } from 'react';
import Cards from '../web_components/Cards';

function Index() {
  const [message, setMessage] = useState('');
  const [userHasFilledForm, setUserHasFilledForm] = useState(false); // Define the state here

  // Simulate user data for testing purposes
  const currentUser = {
    role: 'customer',
  };

  useEffect(() => {
    // Simulate checking if the user has filled the pre_medical form
    if (currentUser.role === 'customer') {
      setUserHasFilledForm(true); // Set to true for testing, replace with actual logic
    }
  }, []);


  const containerStyle = {
    // Define your container styles here
    backgroundColor: 'lightgray',
    padding: '20px',
    textAlign: 'center',
    // Add more styles as needed
  };

  const centeredContentStyle = {
    // Define styles for the centered content div
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    height: '20vh',
    // Add more styles as needed
  };

  const messageStyle = {
    fontSize: '24px',
    color: 'black',
    textAlign: 'center', // Center the text horizontally
    // Add more styles as needed
  };
  

  return (
    <>
      <div></div>
      <div style={containerStyle}>
        <h1 style={messageStyle}>Some Content</h1>
      </div>
      <div><Cards></Cards></div>
      <div style={containerStyle}>
      </div>
      <div style={centeredContentStyle}>

      </div>
    </>
  );
}

export default Index;
