import React, { useState } from 'react';
import LoginForm from '../components/LoginForm'; // Import your LoginForm component
import { loginUser } from '../services/api'; // Import the loginUser function

function LoginComponent() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const submitLogin = (e) => {
    e.preventDefault();

    // Create an object with user login data
    const userData = {
      username,
      password,
    };

    // Call the loginUser function with the userData
    loginUser(userData)
      .then((response) => {
        // Login successful, handle the response as needed
        console.log('Login successful', response);
        // You can also redirect the user to their dashboard or display a success message.
      })
      .catch((error) => {
        // Login failed, handle the error
        console.error('Login error', error);
        // You can display an error message to the user.
      });
  };

  return (
    <div>
      <h2>Login</h2>
      <LoginForm
        username={username}
        password={password}
        setUsername={setUsername}
        setPassword={setPassword}
        submitLogin={submitLogin}
      />
    </div>
  );
}

export default LoginComponent;
