// CombinedForm.js
import React, { useState } from 'react';
import { useAuth } from '../AuthContext';
import { fillPreMedicalForm, createBlogPost } from '../apiService';
import './CombinedForm.css'; // Import the stylesheet

const CombinedForm = () => {
  const { user } = useAuth();

  // State for FillPreMedicalForm
  const [symptoms, setSymptoms] = useState('');

  // State for CreatePost
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');

  // Handle submission for both forms
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Check which form is being submitted
      if (title && content) {
        await createBlogPost({ title, content, author: user.id });
        // You may want to add a success message or redirect to the user's posts
      } else if (symptoms) {
        await fillPreMedicalForm({ symptoms, patient: user.id });
        // You may want to add a success message or redirect to a confirmation page
      }
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="container mt-5">
      <div className="form-container mt-5">
        <h2>Create a Post or Fill Pre-Medical Form</h2>
        <form className="combined-form" onSubmit={handleSubmit}>
          {/* CreatePost fields */}
          <label>Title:</label>
          <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} />
          <label>Content:</label>
          <textarea value={content} onChange={(e) => setContent(e.target.value)} />

          {/* FillPreMedicalForm fields */}
          <label>Symptoms:</label>
          <textarea value={symptoms} onChange={(e) => setSymptoms(e.target.value)} />

          <button type="submit">Submit</button>
        </form>
      </div>
    </div>
  );
};

export default CombinedForm;
