import React, { useState } from 'react';
import { deleteUser, deleteBlogPost, deletePreMedicalForm, deleteComment, getAllUsers, getPatientBlogPosts, getPatientSymptoms, getComments } from '../../apiService';

const AdminDashboard = () => {
    const [users, setUsers] = useState([]);
    const [blogPosts, setBlogPosts] = useState([]);
    const [symptoms, setSymptoms] = useState([]);
    const [comments, setComments] = useState([]);

    const handleDeleteUser = async (userId) => {
        try {
            await deleteUser(userId);
            // Reload the list of users after deletion
            const updatedUsers = await getAllUsers();
            setUsers(updatedUsers);
        } catch (error) {
            console.error('Error deleting user:', error);
        }
    };

    const handleDeleteBlogPost = async (blogPostId) => {
        try {
            await deleteBlogPost(blogPostId);
            // Reload the list of blog posts after deletion
            const updatedBlogPosts = await getPatientBlogPosts();
            setBlogPosts(updatedBlogPosts);
        } catch (error) {
            console.error('Error deleting blog post:', error);
        }
    };

    const handleDeletePreMedicalForm = async (formId) => {
        try {
            await deletePreMedicalForm(formId);
            // Reload the list of symptoms after deletion
            const updatedSymptoms = await getPatientSymptoms();
            setSymptoms(updatedSymptoms);
        } catch (error) {
            console.error('Error deleting pre-medical form:', error);
        }
    };

    const handleDeleteComment = async (commentId, blogPostId) => {
        try {
            await deleteComment(commentId);
            // Reload the list of comments after deletion
            if (blogPostId) {
                const updatedComments = await getComments(blogPostId);
                setComments(updatedComments);
            }
        } catch (error) {
            console.error('Error deleting comment:', error);
        }
    };



    // Fetch initial data on component mount
    const fetchData = async () => {
        try {
            const usersData = await getAllUsers();
            const blogPostsData = await getPatientBlogPosts();
            const symptomsData = await getPatientSymptoms();
            const commentsData = await getComments();

            setUsers(usersData);
            setBlogPosts(blogPostsData);
            setSymptoms(symptomsData);
            setComments(commentsData);
        } catch (error) {
            console.error('Error fetching initial data:', error);
        }
    };

    return (
        <div>
            <h2>Admin Dashboard</h2>
            <button onClick={fetchData}>Refresh Data</button>

            <h3>Users</h3>
            <ul>
                {users.map((user) => (
                    <li key={user.id}>
                        {user.username} - <button onClick={() => handleDeleteUser(user.id)}>Delete</button>
                    </li>
                ))}
            </ul>

            <h3>Blog Posts</h3>
            <ul>
                {blogPosts.map((post) => (
                    <li key={post.id}>
                        {post.title} - <button onClick={() => handleDeleteBlogPost(post.id)}>Delete</button>
                    </li>
                ))}
            </ul>

            <h3>Symptoms</h3>
            <ul>
                {symptoms.map((symptom) => (
                    <li key={symptom.id}>
                        {symptom.symptoms} - <button onClick={() => handleDeletePreMedicalForm(symptom.id)}>Delete</button>
                    </li>
                ))}
            </ul>

            <h3>Comments</h3>
            <ul>
                {comments.map((comment) => (
                    <li key={comment.id}>
                        {comment.content} - <button onClick={() => handleDeleteComment(comment.id, comment.blog_post)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default AdminDashboard;
