// apiService.js
import axios from 'axios';

axios.defaults.xsrfCookieName = 'csrftoken';
axios.defaults.xsrfHeaderName = 'X-CSRFToken';
axios.defaults.withCredentials = true;

const BASE_URL = 'http://localhost:8000';

const apiService = axios.create({
  baseURL: BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Wrap API calls in a try-catch block to handle errors uniformly
const handleRequest = async (request) => {
  try {
    const response = await request;
    return response.data;
  } catch (error) {
    throw error.response.data;
  }
};

// Export functions for various API calls
export const login = async (username, password) => {
  return handleRequest(apiService.post('/login/', { username, password }));
};

export const logout = async () => {
  return handleRequest(apiService.post('/logout/'));
};

export const getUserDetails = async () => {
  return handleRequest(apiService.get('/user-details/'));
};

export default apiService;
