import React, { useState } from 'react';
import Offcanvas from 'react-bootstrap/Offcanvas';
import Button from 'react-bootstrap/Button';

const CommentsOffCanvas = ({ comments, onHide }) => {
  return (
    <Offcanvas show={comments.length > 0} onHide={onHide}>
      <Offcanvas.Header closeButton>
        <Offcanvas.Title>Comments</Offcanvas.Title>
      </Offcanvas.Header>
      <Offcanvas.Body>
        {comments.map((comment) => (
          <div key={comment.id}>
            <p>{comment.content}</p>
            {/* Add more details about the comment like author, timestamp, etc. */}
          </div>
        ))}
      </Offcanvas.Body>
    </Offcanvas>
  );
};

const CommentButton = ({ onClick }) => {
  return (
    <Button variant="primary" onClick={onClick}>
      Show Comments
    </Button>
  );
};

const CommentSection = ({ comments }) => {
  const [showComments, setShowComments] = useState(false);

  const handleShowComments = () => {
    setShowComments(true);
  };

  const handleHideComments = () => {
    setShowComments(false);
  };

  return (
    <div>
      <CommentButton onClick={handleShowComments} />
      <CommentsOffCanvas comments={comments} onHide={handleHideComments} />
    </div>
  );
};

export default CommentSection;
