// CombinedForm.js
import React, { useState } from 'react';
import { useAuth } from '../../AuthContext';
import { fillPreMedicalForm, createBlogPost } from '../../apiService';
import './CombinedForm.css'; // Import the stylesheet
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

const CombinedForm = () => {
  const { user } = useAuth();

  // State for FillPreMedicalForm
  const [symptoms, setSymptoms] = useState('');

  // State for CreatePost
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');

  // Handle submission for both forms
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Check if the user has filled out the PreMedicalForm
      const hasFilledPreMedicalForm = user.preMedicalFormFilled;
  
      // If the user has filled out the PreMedicalForm, allow them to submit the BlogPost form
      if (hasFilledPreMedicalForm && title && content) {
        await createBlogPost({ title, content, author: user.id });
        // You may want to add a success message or redirect to the user's posts
      } else if (!hasFilledPreMedicalForm && symptoms) {
        // If the user hasn't filled out the PreMedicalForm, allow them to submit only the PreMedicalForm
        await fillPreMedicalForm({ symptoms, patient: user.id });
        // You may want to add a success message or redirect to a confirmation page
      } else {
        // Handle the case where the user is missing required information
        console.error('Please fill out the required form fields.');
      }
    } catch (error) {
      console.error(error);
    }
  };
  

  return (
    <div className="container mt-5">
      <div className="form-container mt-5">
        <h2 className="mb-4">Create a Post and Fill Pre-Medical Form</h2>
        <Form className="combined-form" onSubmit={handleSubmit}>
          {/* CreatePost fields */}
          <Form.Group className="mb-3">
            <Form.Label>Title:</Form.Label>
            <Form.Control type="text" value={title} onChange={(e) => setTitle(e.target.value)} />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Content:</Form.Label>
            <Form.Control as="textarea" rows={3} value={content} onChange={(e) => setContent(e.target.value)} />
          </Form.Group>

          {/* FillPreMedicalForm fields */}
          <Form.Group className="mb-3">
            <Form.Label>Symptoms:</Form.Label>
            <Form.Control as="textarea" rows={3} value={symptoms} onChange={(e) => setSymptoms(e.target.value)} />
          </Form.Group>

          <Button variant="primary" type="submit">
            Submit
          </Button>
        </Form>
      </div>
    </div>
  );
};

export default CombinedForm;
