// apiService.js
import axios from 'axios';

axios.defaults.xsrfCookieName = 'csrftoken';
axios.defaults.xsrfHeaderName = 'X-CSRFToken';
axios.defaults.withCredentials = true;
const BASE_URL = 'http://127.0.0.1:8000/';  // Replace with your Django backend URL

const apiService = axios.create({
  baseURL: BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Function to set the authentication token in the headers
const setAuthToken = (token) => {
  if (token) {
    apiService.defaults.headers.common['Authorization'] = `Token ${token}`;
  } else {
    delete apiService.defaults.headers.common['Authorization'];
  }
};

export const login = async (username, password) => {
  try {
    const response = await apiService.post('/login/', { username, password });
    const token = response.data.token;  // Assuming your token is returned in the response
    setAuthToken(token);
    return response.data;
  } catch (error) {
    // Handle errors
  }
};

export const logout = async () => {
  try {
    const response = await apiService.post('/logout/');
    setAuthToken(null);  // Clear the token on logout
    return response.data;
  } catch (error) {
    // Handle errors
  }
};

export const getUserDetails = async () => {
  try {
    const response = await apiService.get('/user-details/');
    return response.data;
  } catch (error) {
    // Handle errors
  }
};

export const createBlogPost = async (data) => {
    try {
      const response = await apiService.post('/create-blog-post/', data);
      return response.data;
    } catch (error) {
      console.error(error);
      throw error;
    }
  };
  
