// App.js
import React from 'react';
import { AuthProvider, useAuth } from './web/AuthContext';
import { BrowserRouter as Router, Route, Switch, Redirect } from 'react-router-dom';
import Navbar from './web/Design/Navbar';
import Login from './web/cmpts/Login';
import Logout from './web/cmpts/Logout';
import Patient from './web/cmpts/Patient';
import Doctor from './web/cmpts/Doctor';
import Admin from './web/cmpts/Admin';

function App() {
  const { user } = useAuth();

  return (
    <Router>
      <AuthProvider>
        <Navbar />
        <Switch>
          <Route path="/login" component={Login} />
          <Route path="/logout" component={Logout} />
          <PrivateRoute path="/patient" component={Patient} role="patient" />
          <PrivateRoute path="/doctor" component={Doctor} role="doctor" />
          <PrivateRoute path="/admin" component={Admin} role="admin" />
          <Redirect from="/" to="/login" />
        </Switch>
      </AuthProvider>
    </Router>
  );
}

// PrivateRoute component to handle role-based redirects
function PrivateRoute({ component: Component, role, ...rest }) {
  const { user } = useAuth();

  return (
    <Route
      {...rest}
      render={(props) => {
        // If the user is logged in and has the correct role, render the component
        if (user && user.role === role) {
          return <Component {...props} />;
        }
        // If the user is not logged in, redirect to the login page
        return <Redirect to="/login" />;
      }}
    />
  );
}

export default App;
