// AuthContext.js
import React, { createContext, useContext, useState, useEffect } from 'react';
import { login, logout, getUserDetails } from './apiService';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);

  // Use useEffect to fetch user details only on mount
  useEffect(() => {
    const fetchUser = async () => {
      try {
        const data = await getUserDetails();
        console.log('User details on mount:', data);
        setUser(data);
      } catch (error) {
        console.error('Fetch user details on mount failed:', error);
      }
    };

    if (!user) {
      fetchUser();
    }
  }, []); // Empty dependency array ensures the effect runs only on mount

  const handleLogin = async (username, password) => {
    try {
      const data = await login(username, password);
      console.log('Login data:', data);
      setUser(data);
    } catch (error) {
      console.error('Login failed:', error);
    }
  };

  const handleLogout = async () => {
    try {
      await logout();
      setUser(null);
    } catch (error) {
      console.error('Logout failed:', error);
    }
  };

  const fetchUserDetails = async () => {
    try {
      const data = await getUserDetails();
      console.log('User details:', data);
      setUser(data);
    } catch (error) {
      console.error('Fetch user details failed:', error);
    }
  };

  return (
    <AuthContext.Provider value={{ user, handleLogin, handleLogout, fetchUserDetails }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);
