// AdminPage.js
import React, { useState, useEffect } from 'react';
import { useAuth } from '../../AuthContext';
import { getAllUsers, promoteUser } from '../../apiService';
import Table from 'react-bootstrap/Table';
import Button from 'react-bootstrap/Button';

const AdminPage = () => {
  const { user } = useAuth();
  const [users, setUsers] = useState([]);

  useEffect(() => {
    fetchAllUsers();
  }, []);

  const fetchAllUsers = async () => {
    try {
      const allUsers = await getAllUsers();
      setUsers(allUsers);
    } catch (error) {
      console.error(error);
    }
  };

  const handlePromoteUser = async (userId) => {
    try {
      await promoteUser(userId);
      // Update the user list after promoting a user
      fetchAllUsers();
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="container mt-5">
      <h2 className="mb-4">Admin Panel</h2>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>User ID</th>
            <th>Username</th>
            <th>Role</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user.id}>
              <td>{user.id}</td>
              <td>{user.username}</td>
              <td>{user.role}</td>
              {user.role !== 'admin' && (
                <td>
                  <Button variant="success" onClick={() => handlePromoteUser(user.id)}>
                    Promote to Admin
                  </Button>
                </td>
              )}
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
};

export default AdminPage;
