import React, { useState, useEffect } from 'react';
import {
  deleteUser,
  deleteBlogPost,
  deletePreMedicalForm,
  deleteComment,
  getAllUsers,
  getPatientBlogPosts,
  getPatientSymptoms,
  getComments,
} from '../../apiService';
import './AdminDashboard.css'; // Create a CSS file for styling
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [blogPosts, setBlogPosts] = useState([]);
  const [symptoms, setSymptoms] = useState([]);
  const [comments, setComments] = useState([]);

  const fetchData = async () => {
    try {
      const usersData = await getAllUsers();
      const blogPostsData = await getPatientBlogPosts();
      const symptomsData = await getPatientSymptoms();
      const commentsData = await getComments(blogPostsData.map((post) => post.id));

      setUsers(usersData);
      setBlogPosts(blogPostsData);
      setSymptoms(symptomsData);
      setComments(commentsData);
    } catch (error) {
      console.error('Error fetching initial data:', error);
    }
  };

  const handleDeleteUser = async (userId) => {
    try {
      await deleteUser(userId);
      fetchData();
    } catch (error) {
      console.error('Error deleting user:', error);
    }
  };

  const handleDeleteBlogPost = async (blogPostId) => {
    try {
      await deleteBlogPost(blogPostId);
      fetchData();
    } catch (error) {
      console.error('Error deleting blog post:', error);
    }
  };

  const handleDeletePreMedicalForm = async (formId) => {
    try {
      await deletePreMedicalForm(formId);
      fetchData();
    } catch (error) {
      console.error('Error deleting pre-medical form:', error);
    }
  };

  const handleDeleteComment = async (commentId, blogPostId) => {
    try {
      await deleteComment(commentId);
      fetchData();
    } catch (error) {
      console.error('Error deleting comment:', error);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div className="admin-dashboard-container">
      <h2 className="text-center">Admin Dashboard</h2>
      <button className="btn btn-primary" onClick={fetchData}>
        Refresh Data
      </button>

      <div className="user-cards-container">
        {users.map((user) => (
          <Card key={user.id} className="user-card">
            <Card.Header>
              <h3>{user.username}</h3>
              <Button variant="danger" size="sm" onClick={() => handleDeleteUser(user.id)}>
                Delete User
              </Button>
            </Card.Header>
            <Card.Body>
              <h4>Blog Posts:</h4>
              <ul>
                {blogPosts
                  .filter((post) => post.author === user.id)
                  .map((post) => (
                    <li key={post.id}>
                      {post.title} -{' '}
                      <Button variant="danger" size="sm" onClick={() => handleDeleteBlogPost(post.id)}>
                        Delete Blog Post
                      </Button>
                    </li>
                  ))}
              </ul>

              <h4>Pre-Medical Forms:</h4>
              <ul>
                {symptoms
                  .filter((symptom) => symptom.patient === user.id)
                  .map((symptom) => (
                    <li key={symptom.id}>
                      {symptom.symptoms} -{' '}
                      <Button variant="danger" size="sm" onClick={() => handleDeletePreMedicalForm(symptom.id)}>
                        Delete Pre-Medical Form
                      </Button>
                    </li>
                  ))}
              </ul>
            </Card.Body>
          </Card>
        ))}
      </div>
    </div>
  );
};

export default AdminDashboard;
