// PatientPostView.js
import React, { useState, useEffect } from 'react';
import { useAuth } from '../AuthContext';
import { getMessages, createMessage } from '../apiService';

const PatientPostView = () => {
    const { user } = useAuth();
    const [messages, setMessages] = useState([]);
    const [selectedDoctorId, setSelectedDoctorId] = useState(null);

  useEffect(() => {
    // Fetch messages when the component mounts
    fetchMessages();
  }, []); // Empty dependency array means this effect runs once when the component mounts

  const fetchMessages = async () => {
    try {
      const receivedMessages = await getMessages();
      setMessages(receivedMessages);
    } catch (error) {
      console.error(error);
    }
  };

  const handleMessage = async (content) => {
    try {
      // Check if a doctor is selected
      if (!selectedDoctorId) {
        console.error('Please select a doctor before sending a message.');
        return;
      }
  
      await createMessage({ content, sender: user.id, receiver: selectedDoctorId });
      // You may want to add a success message or update the state to reflect the message
    } catch (error) {
      console.error(error);
    }
  };
  
  const handleDoctorSelection = (doctorId) => {
    setSelectedDoctorId(doctorId);
  };
  return (
    <div>
      <h2>Your Messages</h2>
      {messages.map((message) => (
        <div key={message.id}>
          <p>{message.content}</p>
        </div>
      ))}
      <textarea placeholder="Type your message" />
      {/* Assume you have a way to select a doctor, and update the selectedDoctorId state */}
      <button onClick={() => handleDoctorSelection(/* actual doctor's id */)}>Select Doctor</button>
      <button onClick={() => handleMessage('content')}>Send Message</button>
    </div>
  );
};

export default PatientPostView;
