// DoctorPage.js
import React, { useState, useEffect } from 'react';
import { useAuth } from '../AuthContext';
import { getPatientPosts, replyToPreMedicalForm, createComment } from '../apiService';

const DoctorPage = () => {
  const { user } = useAuth();
  
  const [patientPosts, setPatientPosts] = useState([]);
  const [commentContent, setCommentContent] = useState('');

  useEffect(() => {
    fetchPatientPosts();
  }, []);

  const fetchPatientPosts = async () => {
    try {
      const posts = await getPatientPosts();
      setPatientPosts(posts);
    } catch (error) {
      console.error(error);
    }
  };

  const handleReply = async (postId, doctorUserId) => {
    try {
      await replyToPreMedicalForm(postId);
      // Set the selected doctor's ID when replying to the post
      setSelectedDoctorId(doctorUserId);
      // You may want to add a success message or update the state to reflect the reply
    } catch (error) {
      console.error(error);
    }
  };
  

  const handleComment = async (postId, patientUserId) => {
    try {
        await createComment({
            content: commentContent,
            author: user.id,
            blog_post: postId,
            commenter_role: 'patient',
            receiver: patientUserId,
        });

      // You may want to add a success message or update the state to reflect the comment

      // Clear the comment content after posting
      setCommentContent('');
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h2>Patient Posts</h2>
      {patientPosts.map((post) => (
        <div key={post.id}>
          <h3>{post.title}</h3>
          <p>{post.content}</p>
          {post.comments && post.comments.map((comment) => (
            <div key={comment.id}>
              <p>{comment.content}</p>
              <p>Doctor ID: {comment.author}</p> {/* Display the doctor's ID */}
            </div>
          ))}
          {!post.form_filled_by_doctor && (
            <button onClick={() => handleReply(post.id)}>Reply to Pre-Medical Form</button>
          )}
          <textarea
            placeholder="Type your comment"
            value={commentContent}
            onChange={(e) => setCommentContent(e.target.value)}
          />
          <button onClick={() => handleComment(post.id, post.patient)}>Comment</button>
        </div>
      ))}
    </div>
  );
};

export default DoctorPage;
