import React, { useState } from 'react';
import RegisterForm from '../components/RegisterForm'; // Import your RegisterForm component
import { registerUser } from '../services/api'; // Import the registerUser function

function RegistrationPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [username, setUsername] = useState('');
  const [role, setRole] = useState('patient'); // Set the default role to 'patient'

  const submitRegistration = (e) => {
    e.preventDefault();

    // Create an object with user registration data
    const userData = {
      email,
      password,
      username,
      role,
    };

    // Call the registerUser function with the userData
    registerUser(userData)
      .then((response) => {
        // Registration successful, handle the response as needed
        console.log('Registration successful', response);
        // You can also redirect the user to the login page or display a success message.
      })
      .catch((error) => {
        // Registration failed, handle the error
        console.error('Registration error', error);
        // You can display an error message to the user.
      });
  };

  return (
    <div>
      <h2>User Registration</h2>
      <RegisterForm
        email={email}
        password={password}
        username={username}
        role={role}
        setEmail={setEmail}
        setPassword={setPassword}
        setUsername={setUsername}
        setRole={setRole}
        submitRegistration={submitRegistration}
      />
    </div>
  );
}

export default RegistrationPage;
