// App.js
import React from 'react';
import { AuthProvider, useAuth } from './web/AuthContext';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Navbar from './web/Design/Navbar';
import Login from './web/cmpts/Login';
import Logout from './web/cmpts/Logout';
import Patient from './web/Users/Patient';
import Doctor from './web/Users/Doctor';
import Admin from './web/Users/Admin';

const PrivateRoute = ({ element, role }) => {
  const { user } = useAuth();

  if (!user) {
    // If the user is not logged in, redirect to the login page
    return <Navigate to="/login" />;
  }

  switch (role) {
    case 'patient':
      return user.role === 'patient' ? <>{element}</> : <Navigate to="/login" />;
    case 'doctor':
      return user.role === 'doctor' ? <>{element}</> : <Navigate to="/login" />;
    case 'admin':
      return user.role === 'admin' ? <>{element}</> : <Navigate to="/login" />;
    default:
      return <Navigate to="/login" />;
  }
};

function App() {
  return (
    <Router>
      <AuthProvider>
        <Navbar />
        <Routes>
          <Route path="/login" element={<Login />} />
          <Route path="/logout" element={<Logout />} />
          <Route path="/patient/*" element={<PrivateRoute element={<Patient />} role="patient" />} />
          <Route path="/doctor/*" element={<PrivateRoute element={<Doctor />} role="doctor" />} />
          <Route path="/admin/*" element={<PrivateRoute element={<Admin />} role="admin" />} />



        </Routes>
      </AuthProvider>
    </Router>
  );
}

export default App;
