// CombinedForm.js
import React, { useState } from 'react';
import { useAuth } from '../../AuthContext';
import { fillPreMedicalForm, createBlogPost } from '../../apiService';
import './CombinedForm.css';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

const symptomsList = [
  'Restlessness',
  'Uncontrollable feelings of worry',
  'Increased irritability',
  'Difficulty concentrating',
  'Sleep difficulties',
  'Feeling very sad, hopeless or worried. Children and adolescents with depression may be irritable rather than sad',
  'Low Energy',
  'Having thoughts of self-harm or suicide',
  'Being easily irritated or frustrated.',
  'Eating too much or too little, which may result in weight gain or weight loss',
  'Experiencing physical issues like headache, stomachache or sexual dysfunction',
  'Having the urge to avoid things that trigger anxiety',
  'Having difficulty controlling worry',
  'Having trouble sleeping',
  'Having an increased heart rate',
  'Having a sense of impending danger, panic or doom',
  'Trouble concentrating or thinking about anything other than the present worry',
];

const countryOptions = [
  'Afghanistan',
  'Albania',
  'Algeria',
  'American Samoa',
  'Andean Latin America',
  'Andorra',
  'Angola',
  'Antigua and Barbuda',
  'Argentina',
  'Armenia',
  'Australasia',
  'Australia',
  'Austria',
  'Azerbaijan',
  // Add more countries as needed
];

const disordersList = [
  'Schizophrenia',
  'Bipolar disorder',
  'Eating disorders',
  'Anxiety disorders',
  'Drug use disorders',
  'Alcohol use disorders',
];

const CombinedForm = () => {
  const { user } = useAuth();

  const [symptoms, setSymptoms] = useState([]);
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [age, setAge] = useState('');
  const [country, setCountry] = useState('');
  const [gender, setGender] = useState('');
  const [disordersDiagnosed, setDisordersDiagnosed] = useState([]);

  const handleChange = (e) => {
    const { name, value, checked } = e.target;

    if (name === 'symptoms') {
      setSymptoms((prevSymptoms) => {
        if (checked) {
          return [...prevSymptoms, value];
        } else {
          return prevSymptoms.filter((symptom) => symptom !== value);
        }
      });
    } else if (name === 'age') {
      setAge(value);
    } else if (name === 'country') {
      setCountry(value);
    } else if (name === 'gender') {
      setGender(value);
    } else if (name === 'disordersDiagnosed') {
      setDisordersDiagnosed((prevDisorders) => {
        if (checked) {
          return [...prevDisorders, value];
        } else {
          return prevDisorders.filter((disorder) => disorder !== value);
        }
      });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      console.log('Symptoms:', symptoms);
      const preMedicalFormResponse = await fillPreMedicalForm({
        symptoms: symptoms.join(', '),
        patient: user.id,
        age,
        country,
        gender,
        disordersDiagnosed: disordersDiagnosed.join(', '),
      });
      if (preMedicalFormResponse) {
        const blogPostResponse = await createBlogPost({
          title,
          content,
          author: user.id,
        });
        console.log('Pre-Medical Form Response:', preMedicalFormResponse);
        console.log('Blog Post Response:', blogPostResponse);
      }
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="container mt-5">
      <div className="form-container mt-5">
        <h2 className="mb-4">Create a Post and Fill Pre-Medical Form</h2>
        <Form className="combined-form" onSubmit={handleSubmit}>
          <Form.Group className="mb-3">
            <Form.Label>Title:</Form.Label>
            <Form.Control
              type="text"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
            />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Content:</Form.Label>
            <Form.Control
              as="textarea"
              rows={3}
              value={content}
              onChange={(e) => setContent(e.target.value)}
            />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Symptoms</Form.Label>
            {symptomsList.map((symptom) => (
              <Form.Check
                key={symptom}
                type="checkbox"
                label={symptom}
                name="symptoms"
                value={symptom}
                checked={symptoms.includes(symptom)}
                onChange={handleChange}
              />
            ))}
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Age:</Form.Label>
            <Form.Control
              type="text"
              name="age"
              value={age}
              onChange={handleChange}
            />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Country:</Form.Label>
            <Form.Control
              as="select"
              name="country"
              value={country}
              onChange={handleChange}
            >
              <option value="">Select Country</option>
              {countryOptions.map((countryOption) => (
                <option key={countryOption} value={countryOption}>
                  {countryOption}
                </option>
              ))}
            </Form.Control>
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Gender:</Form.Label>
            <Form.Control
              as="select"
              name="gender"
              value={gender}
              onChange={handleChange}
            >
              <option value="">Select Gender</option>
              <option value="male">Male</option>
              <option value="female">Female</option>
              <option value="other">Other</option>
            </Form.Control>
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>
              Have you ever been diagnosed with any of the following disorders?
            </Form.Label>
            {disordersList.map((disorder) => (
              <Form.Check
                key={disorder}
                type="checkbox"
                label={disorder}
                name="disordersDiagnosed"
                value={disorder}
                checked={disordersDiagnosed.includes(disorder)}
                onChange={handleChange}
              />
            ))}
          </Form.Group>

          <Button variant="primary" type="submit">
            Submit
          </Button>
        </Form>
      </div>
    </div>
  );
};

export default CombinedForm;
