import React, { Component } from 'react';
import { connect } from 'react-redux';
import { userActions } from '../';

class DoctorDashboard extends Component {
    constructor(props) {
        super(props);
        this.state = {
            postComments: '', // Comment content for a patient's post
        };

        this.handleCommentChange = this.handleCommentChange.bind(this);
        this.handleCommentSubmit = this.handleCommentSubmit.bind(this);
    }

    handleCommentChange(e) {
        this.setState({ postComments: e.target.value });
    }

    handleCommentSubmit(e, postId) {
        e.preventDefault();
        const { dispatch } = this.props;
        // Dispatch an action to send the comment to the server for the specified post
        const { postComments } = this.state;
        dispatch(userActions.commentOnPost(postId, postComments));
    }

    render() {
        return (
            <div>
                <h2>Doctor Dashboard</h2>
                <div>
                    <h3>View Patient Posts</h3>
                    {/* Fetch and display patient posts */}
                    <ul>
                        {this.props.patientPosts.map((post) => (
                            <li key={post.id}>
                                <p>{post.content}</p>
                                <form onSubmit={(e) => this.handleCommentSubmit(e, post.id)}>
                                    <textarea
                                        rows="4"
                                        cols="50"
                                        value={this.state.postComments}
                                        onChange={this.handleCommentChange}
                                        placeholder="Write your comment here..."
                                    ></textarea>
                                    <button type="submit">Comment</button>
                                </form>
                            </li>
                        ))}
                    </ul>
                </div>
                <div>
                    <h3>Direct Messages</h3>
                    {/* Implement a direct messaging component for doctor-patient communication */}
                    {/* You can use a separate component for direct messaging */}
                    <DirectMessaging />
                </div>
            </div>
        );
    }
}

function mapStateToProps(state) {
    // Include state information you need from Redux, e.g., patientPosts
    return {
        patientPosts: state.patientPosts, // Adjust this based on your Redux store structure
    };
}

const connectedDoctorDashboard = connect(mapStateToProps)(DoctorDashboard);
export { connectedDoctorDashboard as DoctorDashboard };
