import axios from "axios";

const BASE_URL = "http://127.0.0.1:8000/";

const api = axios.create({
  baseURL: BASE_URL,
  timeout: 5000,
  headers: {
    "Content-Type": "application/json",
  // You can add authentication headers if needed.
  },
});

export const registerUser = (userData) => {
  return api.post("register/", userData);
};

export const loginUser = (userData) => {
  return api.post("login/", userData);
};

export const getUserDetails = () => {
  return api.get("user-details/");
};

export const logoutUser = () => {
  return api.post("logout/");
};

export const fillPreMedicalForm = (formData) => {
  return api.post("fill-pre-medical-form/", formData);
};

export const getPreMedicalForm = () => {
  return api.get("get-pre-medical-form/");
};

export const replyToPreMedicalForm = (formId) => {
  return api.patch(`reply-to-pre-medical-form/${formId}/`);
};

export const sendMessage = (messageData) => {
  return api.post("send-message/", messageData);
};

export const getMessages = () => {
  return api.get("get-messages/");
};

export const createBlogPost = (postData) => {
  return api.post("create-blog-post/", postData);
};

export const createComment = (commentData) => {
  return api.post("create-comment/", commentData);
};

export default api;
