// App.js
import React from 'react';
import { AuthProvider, useAuth } from './web/AuthContext';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Navbar from './web/Design/Navbar';
import Login from './web/cmpts/Login';
import Logout from './web/cmpts/Logout';
import Patient from './web/Users/Patient';
import Doctor from './web/Users/Doctor';
import Admin from './web/Users/Admin';
import PatientPage from './web/cmpts/Patient/PatientPgae';
import CombinedForm from './web/cmpts/Patient/CombinedForm';
import { Link } from 'react-router-dom';

const PrivateRoute = ({ role, element }) => {
  const { user } = useAuth();

  if (!user || user.role !== role) {
    // Redirect to login if the user is not logged in or has the wrong role
    return <Navigate to="/login" />; // Use Navigate component to redirect
  }

  return element;
};

const App = () => {
  const { user } = useAuth();

  return (
    <AuthProvider>
      <Router>
        <Navbar />
        <Routes>
          <Route path="/login" element={<Login />} />
          <Route
            path="/patient/*"
            element={
              <PrivateRoute role="patient" element={<Patient />} />
            }
          />
          <Route
            path="/doctor/*"
            element={
              <PrivateRoute role="doctor" element={<Doctor />} />
            }
          />
          <Route
            path="/admin/*"
            element={
              <PrivateRoute role="admin" element={<Admin />} />
            }
          />
          {user && user.role === 'patient' && (
            <>
              <Route path="/patient/create-post" element={<CombinedForm />} />
              <Route path="/patient/patient-page" element={<PatientPage />} />
            </>
          )}
        </Routes>
      </Router>
    </AuthProvider>
  );
};

export default App;

