// App.js
import React, { useEffect, useState } from 'react';
import { AuthProvider, useAuth } from './web/AuthContext';
import Logout from './web/cmpts/Logout';
import Login from './web/cmpts/Login';
import Post from './web/cmpts/CreatePost';
import Form from './web/cmpts/FillPreMedicalForm';
import Doct from './web/cmpts/DoctorPage';

function App() {
  const { user, handleLogin, handleLogout, fetchUserDetails } = useAuth();
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      if (user === null) {
        await handleLogin("username", "password"); // Assuming a default login for fetching user details
        setLoading(false);
      } else {
        setLoading(false);
        fetchUserDetails();
      }
    };

    fetchData();
  }, [user, handleLogin, fetchUserDetails]);

  const handleLoginFormSubmit = async (username, password) => {
    await handleLogin(username, password);
  };

  if (loading) {
    // You can show a loading spinner or any other loading indicator
    return <p>Loading...</p>;
  }

  return (
    <div className="App">

        <>
          <Post />
          <Form />
          <Doct />
          <Logout onLogout={handleLogout} />
        </>

        <Login onLogin={handleLoginFormSubmit} />

    </div>
  );
}

export default App;
