// PrivateRoute.js
import React from 'react';
import { useAuth, Navigate } from 'react-router-dom';

function PrivateRoute({ element, role }) {
  const { user } = useAuth();

  if (user && user.role === role) {
    return <>{element}</>;
  }

  return <Navigate to="/login" />;
}

export default PrivateRoute;
