// CombinedForm.js
import React, { useState } from 'react';
import { useAuth } from '../AuthContext';
import { fillPreMedicalForm, createBlogPost } from '../apiService';
import './CreatePost.css'; // Import the stylesheet

const CombinedForm = () => {
  const { user } = useAuth();

  // State for FillPreMedicalForm
  const [symptoms, setSymptoms] = useState('');

  // State for CreatePost
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');

  // Handle submission for FillPreMedicalForm
  const handlePreMedicalSubmit = async (e) => {
    e.preventDefault();
    try {
      await fillPreMedicalForm({ symptoms, patient: user.id });
      // You may want to add a success message or redirect to a confirmation page
    } catch (error) {
      console.error(error);
    }
  };

  // Handle submission for CreatePost
  const handlePostSubmit = async (e) => {
    e.preventDefault();
    try {
      await createBlogPost({ title, content, author: user.id });
      // You may want to add a success message or redirect to the user's posts
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="container mt-5">
      {/* CreatePost */}
      <div className="form-container mt-5">
        <h2>Create a Post</h2>
        <form className="create-post-form" onSubmit={handlePostSubmit}>
          <label>Title:</label>
          <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} />
          <label>Content:</label>
          <textarea value={content} onChange={(e) => setContent(e.target.value)} />
          <button type="submit">Create Post</button>
        </form>
      </div>
      {/* FillPreMedicalForm */}
      <div className="form-container">
        <h2>Fill Pre-Medical Form</h2>
        <form className="pre-medical-form" onSubmit={handlePreMedicalSubmit}>
          <label>Symptoms:</label>
          <textarea value={symptoms} onChange={(e) => setSymptoms(e.target.value)} />
          <button type="submit">Submit</button>
        </form>
      </div>

    </div>
  );
};

export default CombinedForm;
