import React from 'react';
import Carousel from 'react-bootstrap/Carousel';
// You can create this CSS file for styling

// Import the images dynamically using require.context
const images = require.context('./Unsplash', false, /\.(png|jpe?g|gif|svg)$/);

const carouselImages = [
  {
    id: 1,
    text: 'First slide',
    image: images('./austin-chan-ukzHlkoz1IE-unsplash.jpg').default,
  },
  {
    id: 2,
    text: 'Second slide',
    image: images('./david-walker-lYeuyQZmkJ0-unsplash.jpg').default,
  },
  // Add more images as needed
];

function UncontrolledCarousel() {
  return (
    <Carousel>
      {carouselImages.map((item) => (
        <Carousel.Item key={item.id}>
          <img src={item.image} alt={item.text} />
          <Carousel.Caption>
            <h3>{item.text}</h3>
            <p>Placeholder caption text.</p>
          </Carousel.Caption>
        </Carousel.Item>
      ))}
    </Carousel>
  );
}

export default UncontrolledCarousel;
