// PostForms.js
import React, { useState } from 'react';
import { useAuth } from '../AuthContext';
import { fillPreMedicalForm, createBlogPost } from '../apiService';
import './PostForms.css'; // Import the stylesheet

const FillPreMedicalForm = () => {
  const { user } = useAuth();
  const [symptoms, setSymptoms] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await fillPreMedicalForm({ symptoms, patient: user.id });
      // You may want to add a success message or redirect to a confirmation page
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="form-container">
      <h2>Fill Pre-Medical Form</h2>
      <form className="pre-medical-form" onSubmit={handleSubmit}>
        <label>Symptoms:</label>
        <textarea value={symptoms} onChange={(e) => setSymptoms(e.target.value)} />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

const CreatePost = () => {
  const { user } = useAuth();
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await createBlogPost({ title, content, author: user.id });
      // You may want to add a success message or redirect to the user's posts
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="form-container">
      <h2>Create a Post</h2>
      <form className="create-post-form" onSubmit={handleSubmit}>
        <label>Title:</label>
        <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} />
        <label>Content:</label>
        <textarea value={content} onChange={(e) => setContent(e.target.value)} />
        <button type="submit">Create Post</button>
      </form>
    </div>
  );
};

export { FillPreMedicalForm, CreatePost };
