import './App.css';
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Container from 'react-bootstrap/Container';
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';
import NavDropdown from 'react-bootstrap/NavDropdown';
import ToggleButton from 'react-bootstrap/ToggleButton';
import ToggleButtonGroup from 'react-bootstrap/ToggleButtonGroup';
import Button from 'react-bootstrap/Button';
import Pre_medical from './components/web_components/Forms/Form_Pre';
import User from './components/Auth/User';
import Login from './components/Auth/Login';
import Register from './components/Auth/Register';
import Doct from './components/Auth/Doct';
import Admin from './components/Auth/Admin';
import { BrowserRouter as Router, Route } from 'react-router-dom';

axios.defaults.xsrfCookieName = 'csrftoken';
axios.defaults.xsrfHeaderName = 'X-CSRFToken';
axios.defaults.withCredentials = true;

const client = axios.create({
  baseURL: 'http://127.0.0.1:8000',
});

function App() {
  const [currentUser, setCurrentUser] = useState(null);
  const [email, setEmail] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [selectedRole, setSelectedRole] = useState('customer');
  const [showRegister, setShowRegister] = useState(false);
  const [preMedicalSubmitted, setPreMedicalSubmitted] = useState(false);
  const [userHasFilledForm, setUserHasFilledForm] = useState(false); 

  useEffect(() => {
    const token = localStorage.getItem('authToken');
    if (token) {
      client.defaults.headers.common['Authorization'] = `Token ${token}`;
      client
        .get('/user-details/')
        .then(function (res) {
          setCurrentUser(res.data);

          // Check if the user has filled the pre_medical form
          if (res.data.role === 'customer') {
            client.get('/get-pre-medical-form/').then((response) => {
              setUserHasFilledForm(response.data.has_filled_pre_medical_form);
            });
          }
        })
        .catch(function (error) {
          setCurrentUser(null);
        });
    }
  }, []);  

  function submitLogin(e) {
    e.preventDefault();
    client
      .post('/login/', {
        username: username,
        password: password,
      })
      .then(function (res) {
        const token = res.data.token;
        localStorage.setItem('authToken', token);
        client.defaults.headers.common['Authorization'] = `Token ${token}`;
        setCurrentUser(res.data);

        // Check if the user has filled the pre_medical form
        if (res.data.role === 'customer') {
          client.get('/get-pre-medical-form/').then((response) => {
            setUserHasFilledForm(response.data.has_filled_pre_medical_form);
          });
        }

        window.location.reload();
      })
      .catch(function (error) {
        console.log(error);
      });
  }

  function submitLogout() {
    client
      .post('/logout/') // Update the endpoint if needed
      .then(() => {
        localStorage.removeItem('authToken');
        delete client.defaults.headers.common['Authorization'];
        setCurrentUser(null);
      })
      .catch((error) => {
        console.error('Logout error:', error);
      });
  }


  function submitRegister(e) {
    e.preventDefault();
    client
      .post('/register/', {
        username: username,
        email: email,
        password: password,
        role: selectedRole,
      })
      .then(function (res) {
        setCurrentUser(res.data);
      })
      .catch(function (error) {
        console.log(error);
      });
  }
  function handlePatientFormSubmit(patientData) {
    client
      .post('/fill-pre-medical-form/', patientData) // Update the endpoint
      .then((response) => {
        console.log('Patient form submitted:', response.data);
        setPreMedicalSubmitted(true);
      })
      .catch((error) => {
        console.error('Error submitting patient form:', error);
      });
  }


  function LogoutButton({ onLogout }) {
    const [isLoading, setLoading] = useState(false);

    useEffect(() => {
      if (isLoading) {
        setTimeout(() => {
          setLoading(false);
          onLogout();
        }, 2000);
      }
    }, [isLoading, onLogout]);

    const handleClick = () => {
      setLoading(true);
    };

    return (
      <Button
        variant="primary"
        disabled={isLoading}
        onClick={!isLoading ? handleClick : null}
      >
        {isLoading ? 'Logging Out...' : 'Logout'}
      </Button>
    );
  }

  return (
    <>
    <div>
      <Navbar expand="lg" className="bg-body-tertiary">
        <Container>
          <Navbar.Brand href="#home">React-Bootstrap</Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="me-auto">
              <Nav.Link href="#home">Home</Nav.Link>
              <Nav.Link href="#link">Link</Nav.Link>
              <NavDropdown title="Dropdown" id="basic-nav-dropdown">
                <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>
                <NavDropdown.Item href="#action/3.2">Another action</NavDropdown.Item>
                <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
                <NavDropdown.Divider />
                <NavDropdown.Item href="#action/3.4">Separated link</NavDropdown.Item>
              </NavDropdown>
            </Nav>
            <Navbar.Collapse className="justify-content-end">
              <Navbar.Text>
                {currentUser ? (
                  <div>
                    <div className="user-info">
                      Signed in as: <a href="#login">{currentUser.username}</a>
                    </div>
                    <div className="logout-button">
                      <LogoutButton onLogout={submitLogout} />
                    </div>
                  </div>
                ) : (
                  'Not Signed In'
                )}
              </Navbar.Text>
            </Navbar.Collapse>
          </Navbar.Collapse>
        </Container>
      </Navbar>
      <div className="center">
        {currentUser ? (
           <>
           <h2>Welcome, {currentUser.username}!</h2>
           {currentUser.role === 'customer' && !preMedicalSubmitted && !userHasFilledForm && (
            // eslint-disable-next-line
             <Pre_medical onSubmit={handlePatientFormSubmit} />
           )}
           {currentUser.role === 'customer' && (preMedicalSubmitted || userHasFilledForm) && <User />}
           {currentUser.role === 'employee' && <Doct />}
           {currentUser.role === 'admin' && <Admin />}
         </>
        ) : (
          <div>
            <ToggleButtonGroup
              type="radio"
              name="login-register"
              defaultValue={showRegister ? 2 : 1}
              onChange={(value) => setShowRegister(value === 2)}
            >
              <ToggleButton id="login-toggle" value={1}>
                Login
              </ToggleButton>
              <ToggleButton id="register-toggle" value={2}>
                Register
              </ToggleButton>
            </ToggleButtonGroup>
            {showRegister ? (
              <div>
                <Register
                  email={email}
                  password={password}
                  username={username}
                  role={selectedRole}
                  setEmail={setEmail}
                  setPassword={setPassword}
                  setUsername={setUsername}
                  setRole={setSelectedRole}
                  submitRegistration={submitRegister}
                />
              </div>
            ) : (
              <div>
                <Login
                  username={username}
                  password={password}
                  setUsername={setUsername}
                  setPassword={setPassword}
                  submitLogin={submitLogin}
                />
              </div>
            )}
          </div>
        )}
      </div>
    </div>
    <div>
    {/* <Router>
      <Route path="/" component={Index} />
    </Router> */}
    </div>
    </>
  );
}

export default App;
