      console.error(error);
      throw error;
    }
  };
  
  export const getPatientBlogPosts = async () => {
    try {
      const response = await apiService.get('/api/blog-posts/'); // Update the endpoint to your actual endpoint
      return response.data;
    } catch (error) {
