    } catch (error) {
      console.error(error);
      throw error;
    }
  };
  
export const getMessages = async () => {
  try {
    const response = await apiService.get('/get-messages/');
    return response.data;
  } catch (error) {
    console.error(error);
    throw error;
  }
};

export const createMessage = async (data) => {
  try {
    const response = await apiService.post('/send-message/', data);
    return response.data;
  } catch (error) {
    console.error(error);
    throw error;
  }
};

