// Inside your React component function

import { useState, useEffect } from 'react';
import { getComments } from '../apiService';  // Update the path based on your project structure

const YourComponent = () => {
  const [comments, setComments] = useState([]);
  const blogPostId = 1;  // Replace with the actual blog post ID

  useEffect(() => {
    fetchComments(blogPostId);
  }, [blogPostId]);

  const fetchComments = async (blogPostId) => {
    try {
      const commentsData = await getComments(blogPostId);
      setComments(commentsData);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h2>Comments</h2>
      <ul>
        {comments.map((comment) => (
          <li key={comment.id}>
            <p>{comment.content}</p>
            {/* Add other comment details as needed */}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default YourComponent;
