// DoctorPage.js
import React, { useState, useEffect } from 'react';
import { useAuth } from '../AuthContext';
import { getPatientPosts, replyToPreMedicalForm, createComment } from '../apiService';

const DoctorPage = () => {
  const { user } = useAuth();
  const [patientPosts, setPatientPosts] = useState([]);

  useEffect(() => {
    // Fetch patient posts when the component mounts
    fetchPatientPosts();
  }, []);

  const fetchPatientPosts = async () => {
    try {
      const posts = await getPatientPosts();
      setPatientPosts(posts);
    } catch (error) {
      console.error(error);
    }
  };

  const handleReply = async (postId) => {
    try {
      await replyToPreMedicalForm(postId);
      // You may want to add a success message or update the state to reflect the reply
    } catch (error) {
      console.error(error);
    }
  };

  const handleComment = async (postId, content, patientUserId) => {
    try {
      await createComment({ content, author: user.id, blog_post: postId, commenter_role: 'doctor', receiver: patientUserId });
      // You may want to add a success message or update the state to reflect the comment
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h2>Patient Posts</h2>
      {patientPosts.map((post) => (
        <div key={post.id}>
          <h3>{post.title}</h3>
          <p>{post.content}</p>
          {post.comments && post.comments.map((comment) => (
            <div key={comment.id}>
              <p>{comment.content}</p>
            </div>
          ))}
          {!post.form_filled_by_doctor && (
            <button onClick={() => handleReply(post.id)}>Reply to Pre-Medical Form</button>
          )}
          <textarea placeholder="Type your comment" />
          <button onClick={() => handleComment(post.id, 'content', post.patient)}>Comment</button>
        </div>
      ))}
    </div>
  );
};

export default DoctorPage;
