// CombinedForm.js
import React, { useState } from 'react';
import { useAuth } from '../../AuthContext';
import { fillPreMedicalForm, createBlogPost } from '../../apiService';
import './CombinedForm.css'; // Import the stylesheet
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

const CombinedForm = () => {
  const { user } = useAuth();

  const [symptoms, setSymptoms] = useState('');
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [formType, setFormType] = useState('blogPost'); // Default to blogPost

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (formType === 'blogPost') {
        await createBlogPost({ title, content, author: user.id });
        // Show success message or redirect
      } else if (formType === 'preMedicalForm') {
        await fillPreMedicalForm({ symptoms, patient: user.id });
        // Show success message or redirect
      }
    } catch (error) {
      console.error(error);
      // Handle errors, e.g., display an error message to the user
    }
  };

  return (
    <div className="container mt-5">
      <div className="form-container mt-5">
        <h2 className="mb-4">Create a Post and Fill Pre-Medical Form</h2>
        <Form className="combined-form" onSubmit={handleSubmit}>
          {formType === 'blogPost' && (
            <>
              <Form.Group className="mb-3">
                <Form.Label>Title:</Form.Label>
                <Form.Control type="text" value={title} onChange={(e) => setTitle(e.target.value)} />
              </Form.Group>
              <Form.Group className="mb-3">
                <Form.Label>Content:</Form.Label>
                <Form.Control as="textarea" rows={3} value={content} onChange={(e) => setContent(e.target.value)} />
              </Form.Group>
            </>
          )}

          {formType === 'preMedicalForm' && (
            <Form.Group className="mb-3">
              <Form.Label>Symptoms:</Form.Label>
              <Form.Control as="textarea" rows={3} value={symptoms} onChange={(e) => setSymptoms(e.target.value)} />
            </Form.Group>
          )}

          <Button variant="primary" type="submit">
            Submit
          </Button>
        </Form>
        <div className="form-toggle">
          <span onClick={() => setFormType('blogPost')}>Create Blog Post</span>
          <span onClick={() => setFormType('preMedicalForm')}>Fill Pre-Medical Form</span>
        </div>
      </div>
    </div>
  );
};

export default CombinedForm;
