import { createStore, applyMiddleware, combineReducers } from 'redux';
import thunkMiddleware from 'redux-thunk';
import { authentication } from './reducers/authentication.reducer'; // Import your authentication reducer
import { registration } from './reducers/registration.reducer'; // Import your registration reducer
import { messages } from './reducers/messages.reducer'; // Import your messages reducer

// Combine your reducers
const rootReducer = combineReducers({
    authentication,
    registration,
    messages,
    // Add more reducers here if you have them
});

// Create the Redux store with combined reducers and middleware
export const store = createStore(
    rootReducer,
    applyMiddleware(thunkMiddleware)
);
