// DoctorPage.js
import React, { useState, useEffect } from 'react';
import { useAuth } from '../AuthContext';
import { getPatientBlogPosts , replyToPreMedicalForm, createComment } from '../apiService';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';

const DoctorPage = () => {
  const { user } = useAuth();
  const [selectedDoctorId, setSelectedDoctorId] = useState(null);
  const [patientData, setPatientData] = useState([]);
  const [commentContent, setCommentContent] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchPatientData();
  }, []);

  const fetchPatientData = async () => {
    try {
      const data = await getPatientBlogPosts();
      setPatientData(data);
    } catch (error) {
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  const handleReply = async (postId, doctorUserId) => {
    try {
      await replyToPreMedicalForm(postId);
      setSelectedDoctorId(doctorUserId);
    } catch (error) {
      console.error(error);
    }
  };

  const handleComment = async (postId, patientUserId) => {
    try {
      await createComment({
        content: commentContent,
        author: user.id,
        blog_post: postId,
        commenter_role: 'doctor',
        receiver: patientUserId,
      });

      setCommentContent('');
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h2>Patient Data</h2>
      {loading ? (
        <p>Loading...</p>
      ) : (
        patientData.map((patient) => (
          <Card key={patient.id} style={{ width: '18rem', marginBottom: '20px' }}>
            <Card.Body>
              <Card.Title>{patient.title}</Card.Title>
              <Card.Text>
                <strong>Patient Symptoms:</strong> {patient.symptoms}<br />
                <strong>Blog Content:</strong> {patient.content}
              </Card.Text>
              {patient.comments && patient.comments.map((comment) => (
                <div key={comment.id} className="comment-container">
                  <p>{comment.content}</p>
                  <p>Patient ID: {comment.author}</p>
                </div>
              ))}
              {!patient.form_filled_by_doctor && (
                <Button onClick={() => handleReply(patient.id)}>Reply to Pre-Medical Form</Button>
              )}
              <textarea
                placeholder="Type your comment"
                value={commentContent}
                onChange={(e) => setCommentContent(e.target.value)}
              />
              <Button onClick={() => handleComment(patient.id, patient.patient)}>Comment</Button>
            </Card.Body>
          </Card>
        ))
      )}
    </div>
  );
};

export default DoctorPage;
