// PatientPostView.js
import React, { useState, useEffect } from 'react';
import { useAuth } from '../AuthContext';
import { getMessages, createMessage, fetchDoctorsFromApi } from '../apiService';

const PatientPostView = () => {
  const { user } = useAuth();
  const [messages, setMessages] = useState([]);
  const [selectedDoctorId, setSelectedDoctorId] = useState(null);
  const [doctors, setDoctors] = useState([]);
  const [loadingDoctors, setLoadingDoctors] = useState(true); // Loading state for fetching doctors

  useEffect(() => {
    // Fetch messages and doctors when the component mounts
    fetchMessages();
    fetchDoctors();
  }, []); // Empty dependency array means this effect runs once when the component mounts

  const fetchMessages = async () => {
    try {
      const receivedMessages = await getMessages();
      setMessages(receivedMessages);
    } catch (error) {
      console.error(error);
    }
  }

  const fetchDoctors = async () => {
    try {
      const doctorsList = await fetchDoctorsFromApi(); // Assuming you have a function to fetch doctors
      setDoctors(doctorsList);
      setLoadingDoctors(false); // Set loading to false once doctors are fetched
    } catch (error) {
      console.error(error);
      setLoadingDoctors(false); // Set loading to false in case of an error
    }
  };

  const handleMessage = async (content) => {
    try {
      // Check if a doctor is selected
      if (!selectedDoctorId) {
        console.error('Please select a doctor before sending a message.');
        return;
      }

      await createMessage({ content, sender: user.id, receiver: selectedDoctorId });
      // You may want to add a success message or update the state to reflect the message
    } catch (error) {
      console.error(error);
    }
  };

  const handleDoctorSelection = (doctorId) => {
    setSelectedDoctorId(doctorId);
  };

  return (
    <div>
      <h2>Your Messages</h2>
      {messages.map((message) => (
        <div key={message.id}>
          <p>{message.content}</p>
        </div>
      ))}
      <textarea placeholder="Type your message" />
      {loadingDoctors ? (
        <p>Loading doctors...</p>
      ) : (
        // Display a list of doctors with a button to select each doctor
        doctors.map((doctor) => (
          <div key={doctor.id}>
            <button onClick={() => handleDoctorSelection(doctor.id)}>Select {doctor.name}</button>
          </div>
        ))
      )}
      <button onClick={() => handleMessage('content')}>Send Message</button>
    </div>
  );
};

export default PatientPostView;
