import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import LoginForm from '../components/LoginForm';
import { loginUser, getUserDetails } from '../services/api';

function LoginComponent() {
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const submitLogin = (e) => {
    e.preventDefault();

    const userData = {
      email,
      password,
    };

    loginUser(userData)
      .then((loginResponse) => {
        console.log('Login successful', loginResponse);

        // Check user details right after login
        getUserDetails()
          .then((userDetailsResponse) => {
            // Assuming the API returns the user's role in the response
            const userRole = userDetailsResponse.data.role;
            console.log('User details:', userDetailsResponse);

            // Redirect to the respective dashboard based on the role
            if (userRole === 'patient') {
              navigate('/patient'); // Redirect to the patient dashboard
            } else if (userRole === 'doctor') {
              navigate('/doctor'); // Redirect to the doctor dashboard
            } else {
              // Handle other roles or cases here
            }
          })
          .catch((userDetailsError) => {
            console.error('User details error', userDetailsError);
          });
      })
      .catch((loginError) => {
        console.error('Login error', loginError);
        // Handle login error
      });
  };


  return (
    <div>
      <h2>Login</h2>
      <LoginForm
        username={username}
        password={password}
        setUsername={setUsername}
        setPassword={setPassword}
        submitLogin={submitLogin}
      />
    </div>
  );
}

export default LoginComponent;
