import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Import the useNavigate hook
import LoginForm from '../components/LoginForm';
import { loginUser } from '../services/api';

function LoginComponent() {
  const navigate = useNavigate(); // Use the useNavigate hook

  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const submitLogin = (e) => {
    e.preventDefault();

    const userData = {
      username,
      password,
    };

    loginUser(userData)
      .then((response) => {
        // Assuming the API returns the user's role in the response
        const userRole = response.data.role;

        console.log('Login successful', response);

        // Redirect to the respective dashboard based on the role
        if (userRole === 'patient') {
          navigate('/patient'); // Redirect to the patient dashboard
        } else if (userRole === 'doctor') {
          navigate('/doctor'); // Redirect to the doctor dashboard
        } else {
          // Handle other roles or cases here
        }
      })
      .catch((error) => {
        console.error('Login error', error);
        // Handle login error
      });
  };

  return (
    <div>
      <h2>Login</h2>
      <LoginForm
        username={username}
        password={password}
        setUsername={setUsername}
        setPassword={setPassword}
        submitLogin={submitLogin}
      />
    </div>
  );
}

export default LoginComponent;
